import { createContext, useContext } from 'react'
import { initializeApp } from 'firebase/app'
import { getAuth, signInWithEmailAndPassword, createUserWithEmailAndPassword, GoogleAuthProvider, signInWithPopup } from 'firebase/auth'
import { getDatabase, set, ref } from 'firebase/database'

const firebaseConfig = {
    apiKey: "AIzaSyAHYPinnc4ivDBbwJJhlEogLvkH8F-Q2dQ",
    authDomain: "user-9f605.firebaseapp.com",
    databaseURL: "https://user-9f605-default-rtdb.firebaseio.com",
    projectId: "user-9f605",
    storageBucket: "user-9f605.appspot.com",
    messagingSenderId: "673944590968",
    appId: "1:673944590968:web:5e1a271a124d8451ef2b8d",
};



const firebaseApp = initializeApp(firebaseConfig);
const firebaseAuth = getAuth(firebaseApp);
const database = getDatabase(firebaseApp);
const googleProvider = new GoogleAuthProvider();


const FirebaseContext = createContext(null);

export const useFirebase = () => useContext(FirebaseContext)

export const FirebaseProvider = (props) => {


    const signupUserWithEamilandPassword = async (email, password) => {
        try {
            await createUserWithEmailAndPassword(firebaseAuth, email, password).then(() => {
                return (alert("Success!!"));
            })

        } catch (error) {
            console.error("Error: ", error);
        }
    }
    const signUpWithGoogle = () => {
        signInWithPopup(firebaseAuth, googleProvider)
    }

    const LogInWithEmailAndPassword = async(eemail, ppassword) => {
        try{
            await signInWithEmailAndPassword(firebaseAuth, eemail, ppassword)
            .then(value=>(console.log("Login Sucess")))
        }catch(error){
            console.log(console.error(error))
        }
    }


    const putData = (key, data) => set(ref(database, key), (data))




    return (
        <FirebaseContext.Provider value={{ signupUserWithEamilandPassword, putData, signUpWithGoogle,LogInWithEmailAndPassword }}>{props.children}</FirebaseContext.Provider>
    )



}
