import "./LoginComponent.css";
import { FaRegUser, FaLock } from "react-icons/fa";
import React, { useState,useEffect } from 'react'
import { useFirebase } from '../context/Firebase';
import { Link } from "react-router-dom";

const LoginComponent = (props) => {
  const firebase = useFirebase();
  const [email, setemail] = useState('')
  const [password, setpassword] = useState('')
  const [member, setmember] = useState('')
  let time = Date.now();
  const [File, setfile] = useState([])
  const loginHandler = () => {
    firebase.LogInWithEmailAndPassword(email,password)


  }
  const [authenBtn, setAuthenBtn] = useState('Signin');

const authbtn=(e)=>{
  setAuthenBtn(authenBtn === 'Signin' ? 'Login' : 'Signin');
}
const getLink = () => {
  return authenBtn === 'Signin' ? '/signin' : '/login';
};

  return (
    <div className="frame-group">

      <div className="sign-in-wrapper">
        <h1 className="sign-in">{props.Authen_title}</h1>
      </div>
      <div className="frame-container">
        <div className="frame-div">
          <div className="rectangle-parent">
            <div className="frame-child" />
            <div className="icon-person-wrapper">
              <FaRegUser className="icon-person" />
            </div>
            <input className="username" placeholder="UserName" type="email" required onChange={(e) => {
              setemail(e.target.value)
            }} />

          </div>
          <div className="rectangle-group">
            <div className="frame-item" />
            <FaLock className="icon-mail-outline" />
            <input className="frame-inner" placeholder="Password" type="password" required
              onChange={(e) => { setpassword(e.target.value) }} />
          </div>
        </div>
        <div className="frame-wrapper">
          <div className="frame-parent1">
            <div className="frame-parent3">
              <input className="rectangle-container-member" value={'Member'} type="checkbox" onClick={(e) => { setmember(e.target.value) }} />
              <div className="member">Member</div>
            </div>

          </div>
        </div>
        <div className="frame-parent4">
          <input className="rectangle-container-file" type="file"
            onClick={(e) => { setfile(e.target.file) }} />

        </div>
        <div className="login-btn">
          <button className="btn" onClick={loginHandler}>Log in </button>
        </div>
        <div className="login-btn">
        <Link to={getLink()} >  <button className="btn" onClick={authbtn} >{authenBtn}</button></Link>
        </div>
      </div>
    </div>

  );
};
export default LoginComponent;
